import React, { Component } from "react";
import CustomButton from "../../../components/UI/Button/Button";
import classes from './ContactData.module.css';
import axios from '../../../axios-order';
import Spinner from "../../../components/UI/Spinner/Spinner";
class ContactData extends Component {
    state = {
        loading: false,
        name: '',
        email: '',
        address: {
            street: '',
            postalCode: '',
        },
    }
    orderHandler = (event) => {
        event.preventDefault();
        this.setState({ loading: true });
        const order = {
            ingredients: this.props.ingredients,
            price: this.state.totalPrice,
            customer: {
                name: "Daegil",
                age: 27,
                address: {
                    street: '77 Finch Ave East',
                    zipCode: "M2N 6H8"
                },
                email: 'pyo920917@gmail.com'
            },
            deliveryMethod: 'fastest'
        }
        axios.post('/orders.json', order).then(response => {
            console.log(response);
            this.setState({ puchasing: false, loading: false, });
            this.props.history.push('/');

        }).catch(err => {
            console.log(err);
            this.setState({ loading: false, puchasing: false, });
        });
    }

    render() {
        let form = (
            <form>
                <input className={classes.input} type="text " name="name" placeholder="Your Name" />
                <input className={classes.input} type="email " name="email" placeholder="Your email" />
                <input className={classes.input} type="text " name="street" placeholder="Your street" />
                <input className={classes.input} type="text " name="postal" placeholder="Your postal" />
                <CustomButton onClick={this.orderHandler} btnType="Success" >ORDER</CustomButton>
            </form>
        );
        if (this.state.loading) {
            form = <Spinner></Spinner>
        }
        return (
            <div className={classes.ContactData}>
                <h4>Enter your Contact Data</h4>
                {form}

            </div>
        );
    }

}

export default ContactData;
