import { Component } from 'react';
// import { useState } from 'react';
import './App.css';
import Person from './Components/Person/Person';
class App extends Component {

  state = {
    persons: [
      { name: 'Daegil', age: 28 },
      { name: 'Emi', age: 32 },
      { name: 'Hazel', age: 22 },
    ],
    isPressed: false,
  };
  switchNameHandler = (newName) => {
    this.setState({
      persons: [
        { name: newName, age: 28 },
        { name: 'Emi', age: 32 },
        { name: 'Hazel', age: 22 },
      ],
    });
  }
  nameChangeHandler = (event) => {
    this.setState({
      persons: [
        { name: event.target.value, age: 28 },
        { name: 'Emi', age: 32 },
        { name: 'Hazel', age: 22 },
      ],
    });
  }
  managePeople = () => {
    const isPressed = this.state.isPressed;
    this.setState(
      {
        isPressed: !isPressed,
      }
    );
  }
  deletePersonHander() {

  }

  render() {
    const style = {
      width: '10%',
      color: 'white',
      backgroundColor: 'blue',
      font: 'inherit',
      border: '1x solid blue',
      padding: '8px',
    }
    let persons = null;
    if (this.state.isPressed) {
      persons = (<div>
        {this.state.persons.map((person, index) => {
          return <Person name={person.name} age={person.age} key={person.name} index={index} onClick={this.deletePersonHander} />
        })}
      </div>);
    }
    return (
      <div className="App">
        <h1>
          Hello world !
      </h1>
        <p>This is really working dude! </p>
        <button style={style} onClick={this.managePeople}>{this.state.isPressed ? "Hide Name" : "Show People"}</button>
        {persons}
      </div>
    );
    // return React.createElement('div', null,
    //   React.createElement('h1', { className: 'App' }, 'Hello World!'));
  }
}




