import Person from "./Person/Person"
import React, { Component } from 'react';

class Persons extends Component {
    //componenet life cycle hooks
    //componenet life cycle hooks


    static getDerivedStateFromProps(props, state) {
        console.log('[Persons.js] getDerivedStateFromProps');
        return state;
    }
    shouldComponentUpdate(nextProps, nextState) {
        console.log('[Persons.js] shouldComponentUpdate');
        return true;
    }

    render() {
        console.log('[Persons.js] rendering...');
        return this.props.isPressed ? this.props.persons.map((person, index) => {
            return (<Person key={person.id} name={person.name}
                age={person.age} onClick={() => this.props.onClick(index)}
                onChange={(event) => this.props.onChange(event, person.id)} />)
        }) : null;
    }
}


export default Persons;
