import { Component } from 'react';
// import { useState } from 'react';
import './App.css';
import Person from './Components/Person/Person';
class App extends Component {

  state = {
    persons: [
      { name: 'Daegil', age: 28 },
      { name: 'Emi', age: 32 },
      { name: 'Hazel', age: 22 },
    ],
    isPressed: false,
  };


  switchNameHandler = (newName) => {
    this.setState({
      persons: [
        { name: newName, age: 28 },
        { name: 'Emi', age: 32 },
        { name: 'Hazel', age: 22 },
      ],
    });
  }
  nameChangeHandler = (event) => {
    this.setState({
      persons: [
        { name: event.target.value, age: 28 },
        { name: 'Emi', age: 32 },
        { name: 'Hazel', age: 22 },
      ],
    });
  }
  managePeople = () => {
    let isPressed = !this.state.isPressed;
    this.setState(
      {
        isPressed: isPressed,
      }
    );
  }
  render() {
    return (
      <div className="App">
        <h1>
          Hello world !
      </h1>
        <p>This is really working dude! </p>
        <button onClick={this.managePeople}>Switch Name</button>
        {!this.state.isPressed ? null : <div>
          <Person name={this.state.persons[0].name} age={this.state.persons[0].age} onClick={() => this.switchNameHandler('Fucker')} />
          <Person name={this.state.persons[1].name} age={this.state.persons[1].age} onClick={this.switchNameHandler.bind(this, 'Shit')} />
          <Person name={this.state.persons[2].name} age={this.state.persons[2].age} />
        </div>}
      </div>
    );
    // return React.createElement('div', null,
    //   React.createElement('h1', { className: 'App' }, 'Hello World!'));
  }
}




